// ---------- Date and Time functions
function getCurrentTime() {
  const today = new Date();
  return `${today.getHours()}:${today.getMinutes()}`;
}
function getCurrentDay() {
  const today = new Date();
  const months = [
    "Janeiro",
    "Fevereiro",
    "Mar√ßo",
    "Abril",
    "Maio",
    "Junho",
    "Julho",
    "Agosto",
    "Setembro",
    "Outubro",
    "Novembro",
    "Dezembro",
  ];
  let valueReturn = "";
  valueReturn += today.getDate();
  valueReturn += ` de ${months[today.getMonth()]} de `;
  valueReturn += today.getFullYear();
  return valueReturn;
}

// ---------- Activity cards
// - Create activity object
function createActivity(id, text, weekDay, time) {
  return { id, text, weekDay, time, isConflict: false, timeHtml: "", cardHtml: "" };
}
// - Check card empty
function checkCardEmpty(cardObject) {
  return cardObject.text !== "";
}
